mapper.select:
select  r.id,r.post_id,r.user_id,r.content,r.reply_time,c.id as c_id, reply_id, to_username, c.user_id as c_user_id, comment_time, comment, to_user_id
from t_comment as c right outer join  t_reply as r on c.reply_id=r.id
where r.id=#{id}

select  r.id,r.post_id,r.user_id,r.content,r.reply_time,c.id as c_id, reply_id, to_username, c.user_id as c_user_id, comment_time, comment, to_user_id
from t_comment as c right outer join  t_reply as r on c.reply_id=r.id
where   post_id=#{id}
limit #{start}, #{num}

select  r.id,r.post_id,r.user_id,r.content,r.reply_time,c.id as c_id, reply_id, to_username, c.user_id as c_user_id, comment_time, comment, to_user_id
from t_comment as c right outer join  t_reply as r on c.reply_id=r.id
where  r.user_id=#{id}
limit #{start}, #{num}
